import logo from './logo.svg';
import './App.css';
import React, { useEffect, useState } from "react";
//const eejs = require('ep_etherpad-lite/node/eejs/'); 
//const settings = require('ep_etherpad-lite/node/utils/Settings');

//exports.eejsBlock_editbarMenuRight = function (hook_name, args, cb) { 
//  args.content = eejs.content="foo"; return cb(); };



function App() {
  const [padID, setPadID] = useState(undefined);


  useEffect(() => {

    let api1 = require('etherpad-lite-client')
    //console.log(api1)
    // alert(api1)
    let etherpad = api1.connect({
      apikey: 'e0ee8e03e729eb36821bc1ccd2a9710adf9bf292841b563bc9f75f3d8df7e92f',
      host: 'localhost',
      port: 9001,
    })
    //http://localhost:9001/
    //192.168.2.13
    //console.log(etherpad)



    etherpad.createGroup(function (error, data) {
      if (error) console.error('Error creating group: ' + error.message)
      else console.log('New group created: ' + data.groupID)

      if (data) {

        var args = {
          groupID: data.groupID,
          padName: 'testpad',
          text: 'Hello world!',
        }
         etherpad.createGroupPad(args, function (error, data) {
          if (error) console.error('Error creating pad: ' + error.message)
          else console.log('New pad created: ' + data.padID)

          if(padID === undefined) {
            setPadID(data.padID)
          }

        })

    etherpad.createPad("test1", "hello")
        }
    })


  }, [])

  console.log(padID)





  return (
    <div className="App">

      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />

{padID && 

        <iframe src={'http://localhost:9001/p/' + padID + '?showChat=false&showLineNumbers=false'} width={600} height={400}></iframe>
}
        <iframe src={'http://localhost:9001/p/' + 'test1' + '?showChat=false&showLineNumbers=false'} width={600} height={400}></iframe>
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
