    host: 'localhost',
    port: 9001,
  })
  //http://localhost:9001/
  //192.168.2.13
  //console.log(etherpad)



  const [padID, setPadID] = useState(undefined);

  etherpad.createGroup(function (error, data) {
    if (error) alert('Error creating group: ' + error.message)
    else console.log('New group created: ' + data.groupID)

    if (data) {

      var args = {
        groupID: data.groupID,
        padName: 'testpad',
        text: 'Hello world!',
      }
       etherpad.createGroupPad(args, function (error, data) {
        if (error) alert('Error creating pad: ' + error.message)
        else console.log('New pad created: ' + data.padID)

        if(!padID) {
        setPadID(data.padID)
        }

      })

      }
  })




  return (
