 // if (error) alert('Error getting html: ' + error.message)
 // else console.log('New pad created: ' + data)

  // some code
//})


let api1 = require('etherpad-lite-client')
//console.log(api1)
// alert(api1)
let etherpad = api1.connect({
  apikey: 'e0ee8e03e729eb36821bc1ccd2a9710adf9bf292841b563bc9f75f3d8df7e92f',
  host: 'localhost',
  port: 9001,
})
//http://localhost:9001/
//192.168.2.13
//console.log(etherpad)



const [padID, setPadID] = useState(undefined);

etherpad.createGroup(function (error, data) {
  if (error) console.error('Error creating group: ' + error.message)
  else console.log('New group created: ' + data.groupID)

  if (data) {

    var args = {
      groupID: data.groupID,
      padName: 'testpad',
      text: 'Hello world!',
    }
     etherpad.createGroupPad(args, function (error, data) {
      if (error) console.error('Error creating pad: ' + error.message)
      else console.log('New pad created: ' + data.padID)

      if(padID === undefined) {
      setPadID(data.padID)
      }

    })

    }
})
function App() {






  return (
    <div className="App">

      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />

{padID && 

        <iframe src={'http://localhost:9001/p/' + padID  + '7l7T9OEHCz1OIHuQHKfI?showChat=false&showLineNumbers=false'} width={600} height={400}></iframe>
}
        <iframe src={'http://pad.test.de/p/testpad#L4?showChat=false&showLineNumbers=false'} width={600} height={400}></iframe>
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
