import * as pad from "../../etherpad/Pad"
import * as author from "../../etherpad/Author"
import { useEffect } from "react"
import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
//import Paper from '@mui/material/Paper';

const UserPads = (props) => {
    const [authorID, setAuthorID] = useState(props.authorID)
    const [padNames, setPadNames] = useState([])
    
useEffect(() => {

    if(authorID) {
        author.ListPadsOfAuthor(authorID).then((data) => {
            console.log("List author pads")
            console.log(data)
            setPadNames(data)           
        }).catch((err) => {
            console.err(err)
        })
    } else {
        pad.listAllPads().then((data) => {
            console.log("List all pads")
            console.log(data)
            setPadNames(data)
        }).catch((err) => {
            console.err(err)
        })
    }
}, [authorID])

//<TableContainer component={Paper}>    
 return(<div>

<TableContainer >
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Dessert (100g serving)</TableCell>
              <TableCell align="right">Calories</TableCell>
              <TableCell align="right">Fat&nbsp;(g)</TableCell>
              <TableCell align="right">Carbs&nbsp;(g)</TableCell>
              <TableCell align="right">Protein&nbsp;(g)</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {padNames?.map((row) => (
              <TableRow
                key={row}
                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}>
                <TableCell align="right">
                    <Link to={"/pad"} component={Pad}>{row}</Link></TableCell>       
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
 </div>)   
    
}


export default UserPads
