import logo from "./logo.svg";
import "./App.css";
import React, { useEffect, useState } from "react";
import * as api from "./api/server"
//const eejs = require('ep_etherpad-lite/node/eejs/');
//const settings = require('ep_etherpad-lite/node/utils/Settings');

//exports.eejsBlock_editbarMenuRight = function (hook_name, args, cb) {
//  args.content = eejs.content="foo"; return cb(); };

// App.js or client.js
function App() {
  const [groupPad, setGroupPad] = useState(undefined)
  const [groupID, setGroupID] = useState(undefined);
  const [authorID, setAuthorID] = useState(undefined);
  const [sessionID, setSessionID] = useState(undefined);
  // group
  useEffect(() => {
    api.createPad().then((res) => {
      console.log("useeffect")
      console.log(res)
      let http = "http://localhost:9001/p/"
      let padID = res.groupPadID
      let sessionID = res.sessionID
      setGroupID(res.groupID)
      setAuthorID(res.authorID)
      setSessionID(res.sessionID)

      document.cookie = `sessionID=${sessionID}`; // The cookies are usually sent from the server
      setGroupPad(`${http}${padID}` + "?showChat=false&showLineNumbers=false")
    })
  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>{groupID}</p>
        <p>{authorID}</p>
        <p>{sessionID}</p>
        <iframe
          src={groupPad}
          width={600}
          height={400}
        ></iframe>
      </header>
    </div>
  );
}

export default App;
