import axios from "axios";

import React, { useEffect, useState } from "react";
//const eejs = require('ep_etherpad-lite/node/eejs/');
//const settings = require('ep_etherpad-lite/node/utils/Settings');

//exports.eejsBlock_editbarMenuRight = function (hook_name, args, cb) {
//  args.content = eejs.content="foo"; return cb(); };
let apiKey = "308c704c36b41c846ba1713a59f92c6a9707ced910894de32070287a03bfcb68"
let apiVersion = 1
let site = "localhost"
let connectionType = "http"
let port = ":9001" // don't forget colon

const endPoint = (point) => `${connectionType}://${site}${port}/api/${apiVersion}/${point}?apikey=${apiKey}`;

export const createAuthor = async () => {
    console.log("Attempting to create Author")

    let createAuthor = 'createAuthor'
    console.log(endPoint(createAuthor))
    //`/api/${apiVersion}/${point}?apikey=${apiKey}`
    //308c704c36b41c846ba1713a59f92c6a9707ced910894de32070287a03bfcb68
    return await axios.get(endPoint(createAuthor)).then((res) => {
        console.error("Post successful")
        console.error(res)

        console.log("Author Created")
    }
)}

export const createPad = async () => {
    console.log("Attempting to create Pad")

    let createAuthor = 'createAuthor'
    let createSession = 'createSession'
    let createGroup = 'createGroup'
    let validUntil = 2222222222;
    let createGroupPadID = 'createGroupPad'

    let authorID = await axios.get(endPoint(createAuthor)).then((res) => {
        console.log("Author Created: " + res.data.data.authorID)
        return res.data.data.authorID
    })
    let groupID = await axios.get(endPoint(createGroup)).then((res) => {
        console.log("Group Created" + res.data.data.groupID)
        return res.data.data.groupID
    })

    
    let sessionEndPoint = `${endPoint(createSession)}` + `&authorID=${authorID}` + `&groupID=${groupID}` +
    `&validUntil=${validUntil}`

    let sessionID = await axios.get(sessionEndPoint).then((res) => {
        console.log(res)
        console.log("Session Created: " + res.data.data.sessionID)
        return res.data.data.sessionID
    })

    let groupPadEndPoint = `${endPoint(createGroupPadID)}` + `&groupID=${groupID}` + `&padName=${"testPadName"}` +
    `&text=this_text_will_NOT_be_encoded_by_curl_use_next_example`
    

    let groupPadID = await axios.get(groupPadEndPoint).then((res) => {
        console.log(res)
        console.log("Group Pad Created: " + res.data.data.padID)
        return res.data.data.padID
    })

    return { "authorID": authorID,
    "sessionID": sessionID,
    "groupID": groupID,
"groupPadID": groupPadID}
}
